import React, { useState } from 'react';

class AuthUser extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            title: "",
            email: "",
            role: "Organisation",
            password: ""
        };
    }
 
    handleSubmit = () => {
        const { title, email, role, password } = this.state;
        axiosInstance.post('/organisation/create-org', {
            email,
            password
        })
        .then(response => {
            console.log(response.data);

            localStorage.setItem('token', response.data.token);
        })
        .catch(error => {
            console.error(error);
        });
    }

  render() {
    return(
    <div className="auth-container">
      <h2>Логин</h2> 
      <div className="form-group">
        <label>Email</label>
        <input 
          type="text" 
          value={email} 
          onChange={(e) => this.setState({ email: e.target.value })} placeholder="Введите почту"
        />
      </div>
      <div className="form-group">
        <label>Пароль</label>
        <input 
          type="password" 
          value={password} 
          onChange={(e) => this.setState({ password: e.target.value })} placeholder="Введите пароль"
        />
      </div>
      <button onClick={handleLogin}>Войти</button>
      <div className="links">
        <a href="/forgot-password">Забыли пароль?</a>
        <span> | </span>
        <a href="/register">Регистрация</a>
      </div>
    </div>
        );
    }
};

export default AuthUser;
