import React from "react";
import AddOrganisation from "./components/addOrganisation";
import AddChecker from "./components/addChecker";
import RoleSelection from "./components/RoleSelection";

class Registration extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      step: 1, // Шаг: 1 - выбор роли, 2 - форма
      role: "", // Выбранная роль
    };
  }

  // Функция для обработки выбора роли
  handleRoleSelect = (role) => {
    this.setState({ role });
  };

  // Переход на следующий шаг
  handleNextStep = () => {
    if (this.state.role) {
      this.setState({ step: 2 });
    }
  };

  // Функция для добавления пользователя
  addUser = (user) => {
    console.log(user);
  };

  render() {
    const { step, role } = this.state;

    // Если шаг 1, показываем компонент выбора роли
    if (step === 1) {
      return (
        <RoleSelection
          onRoleSelect={this.handleRoleSelect} // Передаем функцию выбора роли
          selectedRole={role}
          onNext={this.handleNextStep}
        />
      );
    }

    // Если шаг 2, показываем форму для выбранной роли
    if (step === 2) {
      return (
        <div className="container">
          {role === "Organisation" && <AddOrganisation onAdd={this.addUser} />}
          {role === "Checker" && <AddChecker onAdd={this.addUser} />}
        </div>
      );
    }

    return null;
  }
}

export default Registration;



