import React from "react";
import AddOrganisation from "./components/";
import AddChecker from "./components/AddChecker";
import RoleSelection from "./components/RoleSelection";

class Registration extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      step: 1, // Шаг: 1 - выбор роли, 2 - форма
      role: "" // Выбранная роль
    };
  }

  handleRoleSelect = (role) => {
    this.setState({ role });
  };

  handleNextStep = () => {
    if (this.state.role) {
      this.setState({ step: 2 });
    }
  };

  addUser = (user) => {
    console.log(user);
  };

  render() {
    const { step, role } = this.state;

    if (step === 1) {
      return (
        <RoleSelection
          onRoleSelect={this.handleRoleSelect}
          selectedRole={role}
          onNext={this.handleNextStep}
        />
      );
    }

    if (step === 2) {
      return (
        <div className="container">
          {role === "Organisation" && <AddOrganisation onAdd={this.addUser} />}
          {role === "Checker" && <AddChecker onAdd={this.addUser} />}
        </div>
      );
    }

    return null;
  }
}

export default Registration;
