import React from "react";

const RoleSelection = ({ onRoleSelect, selectedRole, onNext }) => {
  return (
    <div className="role-selection-container">
      <h1 className="role-selection-heading">Добро Пожаловать!</h1>
      <h3 className="role-selection-subheading">Выберите вашу роль</h3>
      <form className="role-selection-form">
        <div className="role-selection-radio-group">
          <div className="role-selection-radio">
            <input
              type="radio"
              name="role"
              value="Organisation"
              checked={selectedRole === "Organisation"}
              onChange={(e) => onRoleSelect(e.target.value)}
              id="organisation"
            />
            <label htmlFor="organisation">Организация</label>
          </div>
          <div className="role-selection-radio">
            <input
              type="radio"
              name="role"
              value="Checker"
              checked={selectedRole === "Checker"}
              onChange={(e) => onRoleSelect(e.target.value)}
              id="checker"
            />
            <label htmlFor="checker">Проверяющий</label>
          </div>
        </div>
        <button
          type="button"
          onClick={onNext}
          className="role-selection-button"
          disabled={!selectedRole}
        >
          Продолжить
        </button>
      </form>
    </div>
  );
};

export default RoleSelection;
