import React from "react";

class RoleSelection extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            role: ""
        }
    }
  render(){
    return (
    <div>
      <h1 >Добро Пожаловать!</h1>
    <h3 >Чтобы начать регистрацию выберите вашу роль</h3>
    <div >
      <h4>Роль<span className="requiredMark">*</span></h4>
      <form>
        <div>
          <input type="radio" name="role"onChange={ this.setState({ tole: "Organisation" })}  id="organizer" />
          <label htmlFor="organizer" className="context">Организатор</label>
        </div>
        <div>
          <input type="radio" name="role" onChange={ this.setState({ tole: "Checker" })} id="checker" />
          <label htmlFor="checker" className="context">Проверяющий</label>
        </div>
        <button type="submit"onClick={() => this.props.onAdd({
            role: this.state.name
          })}>Продолжить</button>
      </form>
    </div>
    </div>
    );
    }
};

export default RoleSelection;
