import React from "react";

const RoleSelection = ({ onRoleSelect, selectedRole, onNext }) => {
  return (
    <div className="">
      <h1 className="titleBox">Добро Пожаловать!</h1>
    <h3 className="miniTitle">Чтобы начать регистрацию выберите вашу роль</h3>
    <div className="formWrapper">
      <h4 className="formLabel">Роль<span className="requiredMark">*</span></h4>
      <form className="registrationForm" action="">
        <div className="radioGroup">
          <input type="radio" name="role" id="organizer" />
          <label htmlFor="organizer" className="context">Организатор</label>
        </div>
        <div className="radioGroup">
          <input type="radio" name="role" id="checker" />
          <label htmlFor="checker" className="context">Проверяющий</label>
        </div>
        <button type="submit" className="submitButton">Продолжить</button>
      </form>
    </div>
    </div>
  );
};

export default RoleSelection;
