import React from "react";
import AddOrganisation from "./components/addOrganisation";
import AddChecker from "./components/addChecker";
import RoleSelection from "./components/RoleSelection";

class Registration extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedRole: null,
      user: null,
    };
  }

  handleRoleSelect = (role) => {
    this.setState({ selectedRole: role });
  };

  handleUserAdd = (user) => {
    this.setState({ user });
    console.log(user);
  };

  render() {
    const { selectedRole, user } = this.state;
    return (
      <div className="container">
        {!selectedRole ? (
          <RoleSelection
            onRoleSelect={this.handleRoleSelect}
            selectedRole={selectedRole}
            onNext={() => this.setState({ selectedRole })}
          />
        ) : selectedRole === "Organisation" ? (
          <AddOrganisation onAdd={this.handleUserAdd} />
        ) : (
          <AddChecker onAdd={this.handleUserAdd} />
        )}
      </div>
    );
  }
}

export default Registration;
